mapper.select:
SELECT a.id AS id, a.create_by AS createBy, a.create_time AS createTime, a.update_by AS updateBy, a.update_time AS updateTime, a.class_code AS classCode, a.speciai_id AS speciaiId, a.title AS title, a.content AS content, a.enabled AS enabled, a.hits_count AS hitsCount, a.reply_count AS replyCount, a.replied_by AS repliedBy, a.replied_time AS repliedTime, a.icon_id AS iconId, a.del_flag AS delFlag, a.public_time AS publicTime, a.edit_time AS editTime, a.sys_org_code AS sysOrgCode, b.id AS imageId, b.topic_image AS topicImage, c.id AS tagId, c.tag AS tag, g.title AS linkTitle, g.link_type AS linkType, g.link_url AS linkUrl, g.parameter AS parameter, g.sort AS linkSort, e.role_name AS userRole, a.STATUS AS STATUS, a.contact AS contact, a.site AS Site, a.topic_type AS topicType, a.hot_reply AS hotReply, a.praise_count AS praiseCount, a.star_count AS starCount, a.region_code AS regionCode, a.region_full_name AS regionFullName, f.realname AS realname, f.avatar AS avatar, f.sex AS sex FROM ( SELECT id, create_by, create_time, update_by, update_time, class_code, speciai_id, title, content, enabled, hits_count, ( SELECT COUNT(1) FROM bbs_reply WHERE bbs_reply.topic_id = bbs_topic.id ) AS reply_count, replied_by, replied_time, icon_id, del_flag, STATUS, contact, site, topic_type, hot_reply, praise_count, star_count, region_code, region_full_name, public_time, edit_time, sys_org_code FROM bbs_topic WHERE region_code = #{regionCode} and status = 1
and class_code = #{classCode}
and topic_type IN
#{topicTypeItem}
ORDER BY public_time DESC LIMIT #{current},#{size} ) a LEFT JOIN bbs_topic_image AS b ON a.id = b.topic_id LEFT JOIN bbs_topic_tag AS c ON a.id = c.topic_id LEFT JOIN bbs_topic_link AS g ON a.id = g.topic_id LEFT JOIN sys_user AS f ON a.create_by = f.username LEFT JOIN sys_user_role AS d ON f.id = d.user_id LEFT JOIN sys_role AS e ON d.role_id = e.id
